-- ----------------------------
-- Table structure for blog_users
-- ----------------------------
DROP TABLE IF EXISTS `blog_users`;
CREATE TABLE `blog_users` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `username` varchar(45) NOT NULL,
  `password` varchar(45) NOT NULL,
  `email` varchar(50) DEFAULT NULL,
  `status` enum('0','1') DEFAULT '0',
  `isadmin` enum('0','1') DEFAULT '0',
  `logtime` int(20) DEFAULT NULL COMMENT '登录时间',
  `logcount` int(10) DEFAULT '0' COMMENT '登录次数',
  `usertel` char(11) DEFAULT NULL COMMENT '电话号',
  `truename` varchar(50) NOT NULL COMMENT '真实姓名',
  `state` tinyint(1) NOT NULL DEFAULT '0',
  PRIMARY KEY (`id`),
  UNIQUE KEY `username` (`username`)
) ENGINE=InnoDB AUTO_INCREMENT=10 DEFAULT CHARSET=utf8;

